$(document).ready(function(){$(".tabs > div").click(function(){var a=$(this).index(),t=$(".content-inner").find("section").eq(a).height();$(".content-inner").stop().animate({height:t+50},200).find("section").eq(a).stop().fadeIn().siblings().fadeOut(),$(this).addClass("active").siblings().removeClass("active");var e=$(this).position().left;$(".tab-bg").stop().animate({left:e})}),$(".pagination > span").click(function(){var a="."+$(this).parent().attr("data-row").toString();if($(this).hasClass("pagination-nav-next")){var t=parseInt($(a).find(".active-p").attr("class").replace(/\D/g,""))+1;if(t>=4)var t=1}else var t=$(this).index()+1;var e=$(a).find(".slide-row-p"+t);e.hasClass("active-p")||($(a).find(".active-p").animate({left:"-100%",opacity:0},500).removeClass("active-p").addClass("inQueue"),e.addClass("active-p").animate({left:0,opacity:1},{duration:500,complete:function(){$(".inQueue").remove().clone().appendTo(a).css({left:"100%",opacity:"0"}).removeClass("inQueue")}}))})});